import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [post, setPost] = React.useState([]);
  const [isEditing, setIsEditing] = useState(false)
  const [message, setMessage] = useState('')
  const [nmessage, nsetMessage] = useState('')
  const editNote = (id) => {
    console.log("edit!");
    axios.put(`/api/menu/items/${id}`, {
      title: message, description: message
    }).then(res => {
      console.log("new text" + message);
    });
    setMessage('');
  };
  const addNote = () => {

    axios.post(`/api/menu/items/`, {
      title: nmessage, description: nmessage
    }).then(res => {
      console.log("new text" + nmessage);
    });
    nsetMessage('');
  };

  const deleteNote = id => {
    axios.delete(`/api/menu/items/${id}`).then(res => {
      console.log(res.data);
    });
  }

  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  if (!post) return null;
  const noteItems = post.map((curpost) =>
    <div>
      {
        isEditing ?
          <div style={{ border: '1px solid red' }} className="e-card e-card-horizontal">
            <form onSubmit={() => editNote(curpost.id)}>
              <input type='text' onChange={event => setMessage(event.target.value)} />
            </form>
            <button onClick={() => editNote(curpost.id)}> Edit</button>
          </div>
          :
          <div style={{ border: '1px solid red' }} className="e-card e-card-horizontal">

            <p onDoubleClick={() => setIsEditing(true)}>{curpost.title}</p>
            <button onClick={() => deleteNote(curpost.id)}> Delete</button>
          </div>
      }
    </div>
  );
  return (
    <div className="e-card e-card-horizontal">
      <ul>{noteItems}</ul>

      <form style={{ border: '1px solid red' }} onSubmit={() => editNote()}>
        <input type='text' onChange={event => nsetMessage(event.target.value)} />
      </form>
      <button onClick={() => addNote()}> Add</button>
    </div>
  );
}
export default App;
