import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

var deleteNote = id => {
  axios.delete(`/api/menu/items/${id}`).then(res => {
    console.log(res.data);
  });
}


function App() {
  const [post, setPost] = React.useState([]);
  const [isEditing, setIsEditing] = useState(false)
  const [message, setMessage] = useState('')

  const handleSubmit = (id) => {

    axios.patch(`/api/menu/items/${id}` + { title: message }).then(res => {
      console.log(res.data);
    });

  };

  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  if (!post) return null;
  const noteItems = post.map((curpost) =>
    <li key="{curpost}">
      {
        isEditing ?
          <div>
            <form>
              <input type='text' onChange={event => setMessage(event.target.value)} />
            </form>
            <button onClick={() => handleSubmit(curpost.id)}> Edit</button>
          </div>
          :
          <div>
            <button onClick={() => deleteNote(curpost.id)}> Delete</button>

            <h1 onDoubleClick={() => setIsEditing(true)}>{curpost.title}</h1>
          </div>
      }
    </li >
  );
  return (
    <ul>{noteItems}</ul>

  );
}
export default App;
