import React, { useEffect, useState } from "react";

import axios from "axios";
import Button from '@mui/material/Button';
function Note(curpost) {
  console.log("created something");
  const [isEditing, setIsEditing] = useState(false)
  const [ctitle, csetTitle] = React.useState(curpost.data.title);
  const [cdesc, csetDesc] = React.useState(curpost.data.description);


  const editNote = (id) => {
    console.log("edit!");
    axios.put(`/api/menu/items/${id}`, {
      title: ctitle, description: cdesc
    }).then(res => {
      updateInfo();
    });

  };

  const updateInfo = () => {
    axios.get(`/api/menu/items/${curpost.data.id}`).then((response) => {
      csetTitle(response.title);
      csetDesc(response.message);
    });
  };


  const deleteNote = id => {
    axios.delete(`/api/menu/items/${id}`).then(res => {

    });
  }

  return (<div>
    {
      isEditing ?
        <div className="card">
          <form onSubmit={() => editNote(curpost.data.id)}>
            <input type="title" onChange={event => csetTitle(event.target.value)} value={ctitle} />
            <textarea onChange={event => csetDesc(event.target.value)} value={cdesc} />
          </form>
          <Button variant="outlined" onClick={() => editNote(curpost.data.id)}> Save </Button>
        </div>
        :
        <div className="card">
          <h2 onDoubleClick={() => setIsEditing(true)}>{ctitle}</h2>
          <p onDoubleClick={() => setIsEditing(true)}>{cdesc}</p>
          <Button variant="outlined" onClick={() => deleteNote(curpost.data.id)}> Delete </Button>
        </div>
    }
  </div>)
}

export default Note;
