import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [post, setPost] = React.useState([]);
  const [isEditing, setIsEditing] = useState(false)

  const [ctitle, csetTitle] = useState('')
  const [cdesc, csetDesc] = useState('')
  const [ntitle, nsetTitle] = useState('')
  const [ndesc, nsetDesc] = useState('')

  const editNote = (id) => {
    console.log("edit!");
    axios.put(`/api/menu/items/${id}`, {
      title: ctitle, description: cdesc
    }).then(res => {

    });
    csetDesc('');
    csetTitle('');
  };
  const addNote = () => {
    axios.post(`/api/menu/items/`, {
      title: ntitle, description: ndesc
    }).then(res => {

    });
    nsetDesc('');
    nsetTitle('');
  };

  const deleteNote = id => {
    axios.delete(`/api/menu/items/${id}`).then(res => {
      console.log(res.data);
    });
  }

  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  if (!post) return null;
  useEffect(() => {
    var noteItems = post.map((curpost) =>
      <div>
        {
          isEditing ?
            <div className="red-border">
              <form onSubmit={() => editNote(curpost.id)}>
                <input type='text' onChange={event => csetTitle(event.target.value)} value={curpost.title} />
                <input type='text' onChange={event => csetDesc(event.target.value)} value={curpost.description} />
              </form>
              <button onClick={() => editNote(curpost.id)}> Edit</button>
            </div>
            :
            <div className="red-border">
              <h2 onDoubleClick={() => setIsEditing(true)}>{curpost.title}</h2>
              <p onDoubleClick={() => setIsEditing(true)}>{curpost.description}</p>
              <button onClick={() => deleteNote(curpost.id)}> Delete</button>
            </div>
        }
      </div>
    );
  }, []);
  return (
    <div >
      <ul>{noteItems}</ul>
      <div className="red-border">
        <h2>Add new note:</h2>
        <form onSubmit={() => editNote()}>
          <input type='text' onChange={event => nsetTitle(event.target.value)} />
          <input type='text' onChange={event => nsetDesc(event.target.value)} />
        </form>
        <button onClick={() => addNote()}> Add</button>
      </div>

    </div>
  );
}
export default App;
