import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Note from "./note";
import Button from '@mui/material/Button';
function App() {
  const [notes, setNotes] = React.useState([]);
  const [newTitle, setNewTitle] = useState('')
  const [newDesc, setNewDescription] = useState('')
  const [notesItems, setNotesItems] = React.useState([]);
  var noteItems;

  const addNote = () => {
    if ((newDesc.length + setNewTitle) > 140) {
      window.alert("Note must be less than 140 characters!");
      return null;
    }
    axios.post(`/api/menu/items/`, {
      title: newTitle, description: newDesc
    }).then(res => {
      axios.get("/api/menu/items/").then((response) => {
        setNotes(response.data);
        notes.map((curnote) => {
          console.log("inside");

        });
      });
    });
    setNewDescription('');
    setNewTitle('');
  };


  useEffect(async () => {
    const { data: response } = await axios.get("/api/menu/items/").then((response) => {
      response.data.map((curnote) => {
        return < Note data={curnote} />
      })
    });
    setNotesItems(response);
  }, []);

  return (
    <div>
      <div className="top">
        <h1>My Notes:</h1>
        <p>Double click on a note's text to edit!</p>
        <i>Made for Dormakaba by Mihail Calitoiu</i>
      </div>

      <ul>{notesItems}</ul>
      <div className="card">
        <h3>Add new note:</h3>
        <form onSubmit={() => addNote()}>
          <input type='title' onChange={event => setNewTitle(event.target.value)} placeholder="Title" value={newTitle} />
          <textarea type='text' onChange={event => setNewDescription(event.target.value)} placeholder="Lorem ipsum dolor etc" value={newDesc} />
        </form>
        <Button variant="outlined" onClick={() => addNote()}> Add </Button>

      </div>

    </div>
  );
}
export default App;
