import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Segment from "./field";
import Button from '@mui/material/Button';

function App() {
  const [post, setPost] = React.useState([]);
  const [ntitle, nsetTitle] = useState('')
  const [ndesc, nsetDesc] = useState('')


  const addNote = () => {
    axios.post(`/api/menu/items/`, {
      title: ntitle, description: ndesc
    }).then(res => {
      reloadComponent()
    });
    nsetDesc('');
    nsetTitle('');
  };


  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  function reloadComponent() {
    window.location.reload(false);
  }

  if (!post) return null;
  var noteItems = post.map((curpost) => {
    console.log("inside");
    return <Segment data={curpost} />
  }

  );

  return (
    <div>
      <div className="top">
        <h1>My Notes:</h1>
        <p>Made for Dormakaba by Mihail</p>
      </div>
      <ul>{noteItems}</ul>
      <div className="card">
        <h3>Add new note:</h3>
        <form onSubmit={() => addNote()}>
          <input type='title' onChange={event => nsetTitle(event.target.value)} placeholder="Title" />
          <textarea type='text' onChange={event => nsetDesc(event.target.value)} placeholder="Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua." />
        </form>
        <Button variant="outlined" onClick={() => addNote()}> Add </Button>
      </div>

    </div>
  );
}
export default App;
