import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

function App() {


  const [message, setMessage] = useState('')
  var [post, setPost] = React.useState([]);
  const editNote = (id) => {
    axios.put(`/api/menu/items${id}` + { title: message, description: message }).then(res => {
      console.log("new text" + message);
    });
  };

  const deleteNote = id => {
    axios.delete(`/api/menu/items/${id}`).then(res => {
      console.log(res.data);
    });
  }

  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  const noteItems = post.map(
    (curpost) => {
      var test = false;
      return (
        <li key="{curpost}">


          {test} ?
          <div>
            <form>
              <input type='text' onChange={event => setMessage(event.target.value)} />
            </form>
            <button onClick={() => editNote(curpost.id)}> Edit</button>
          </div>
          :
          <div>
            <button onClick={() => deleteNote(curpost.id)}> Delete</button>

            <h1 onDoubleClick={() => test = false}>{curpost.title}</h1>
          </div>

        </li >
      )
    });
  return (
    <ul>{noteItems}</ul>

  );
}
export default App;
