import React, { useEffect, useState } from "react";

import axios from "axios";

function Segment(curpost) {
  console.log("created something");
  const [isEditing, setIsEditing] = useState(false)

  const [ctitle, csetTitle] = useForm({
    defaultValues: useMemo(() => {
      return curpost.data.title;
    }, [props])
  });
  const [cdesc, csetDesc] = useState('')


  const editNote = (id) => {
    console.log("edit!");
    axios.put(`/api/menu/items/${id}`, {
      title: ctitle, description: cdesc
    }).then(res => {
      reloadComponent()
    });
    csetDesc('');
    csetTitle('');
  };


  const deleteNote = id => {
    axios.delete(`/api/menu/items/${id}`).then(res => {
      reloadComponent()
    });
  }

  function reloadComponent() {
    window.location.reload(false);

  }

  return (<div>
    {
      isEditing ?
        <div className="red-border">
          <form onSubmit={() => editNote(curpost.data.id)}>
            <input type='text' onChange={event => csetTitle(event.target.value)} value={ctitle} />
            <input type='text' onChange={event => csetDesc(event.target.value)} value={cdesc} />
          </form>
          <button onClick={() => editNote(curpost.data.id)}> Edit</button>
        </div>
        :
        <div className="red-border">
          <h2 onDoubleClick={() => setIsEditing(true)}>{curpost.data.title}</h2>
          <p onDoubleClick={() => setIsEditing(true)}>{curpost.data.description}</p>
          <button onClick={() => deleteNote(curpost.data.id)}> Delete</button>
        </div>
    }
  </div>)
}

export default Segment;
