import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Segment from "./field";

function App() {
  const [post, setPost] = React.useState([]);

  const [ntitle, nsetTitle] = useState('')
  const [ndesc, nsetDesc] = useState('')


  const addNote = () => {
    axios.post(`/api/menu/items/`, {
      title: ntitle, description: ndesc
    }).then(res => {
      reloadComponent()
    });
    nsetDesc('');
    nsetTitle('');
  };


  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  function reloadComponent() {
    window.location.reload(false);
  }

  if (!post) return null;
  var noteItems = post.map((curpost) => {
    console.log("inside");
    return <Segment data={curpost} />
  }

  );
  return (
    <div >
      <ul>{noteItems}</ul>
      <div className="red-border">
        <h2>Add new note:</h2>
        <form onSubmit={() => addNote()}>
          <input type='text' onChange={event => nsetTitle(event.target.value)} />
          <input type='text' onChange={event => nsetDesc(event.target.value)} />
        </form>
        <button onClick={() => addNote()}> Add</button>
      </div>

    </div>
  );
}
export default App;
