import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

var deleteNote = id => {
  axios.delete(`/api/menu/items/${id}`).then(res => {
    console.log(res.data);
  });
}
var editNote = (id, event) => {
  axios.patch(`/api/menu/items/${id}`, { title: message });
}

function App() {
  const [post, setPost] = React.useState([]);
  const [isEditing, setIsEditing] = useState(false)
  const [message, tempzz] = useState('');

  const handleSubmit = (event, id) => {
    event.preventDefault();
    tempzz(axios.patch(`/api/menu/items/${id}`).then(res => {
      console.log(res.data);
    }));
    setFirstName('');
    setLastName('');
  };

  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  if (!post) return null;
  const noteItems = post.map((curpost) =>
    <li key="{curpost}">
      {
        isEditing ?
          <div>
            <form onSubmit={handleSubmit}>
              <input type='text' id="firstName" name="firstName" value={firstName} onChange={(event) => { setLastName(event.target.value, curpost.id); }} defaultValue={curpost.title} />
            </form>
            <button type="submit"> Submit</button>
          </div>
          :
          <div>
            <button onClick={() => deleteNote(curpost.id)}> Delete</button>

            <h1 onDoubleClick={() => setIsEditing(true)}>{curpost.title}</h1>
          </div>
      }
    </li>
  );
  return (
    <ul>{noteItems}</ul>

  );
}
export default App;
