import React, { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";

var deleteNote = id => {
  axios.delete(`/api/menu/items/${id}`).then(res => {
    console.log(res.data);
  });
}
var editNote = (id, event) => {
  axios.patch(`/api/menu/items/${id}`, { title: message });
}

function App() {
  const [post, setPost] = React.useState([]);
  const [isEditing, setIsEditing] = useState(false)
  const [message, setMessage] = useState('');

  handleChange = (event) => {
    // ðŸ‘‡ Get input value from "event"
    setMessage(event.target.value);
  };
  useEffect(() => {
    axios.get("/api/menu/items/").then((response) => {
      setPost(response.data);
    });
  }, []);

  if (!post) return null;
  const noteItems = post.map((curpost) =>
    <li key="{curpost}">
      {
        isEditing ?
          <form>
            <input type='text' onChange={() => editNote(curpost.id)} defaultValue={curpost.title} />
          </form>
          :
          <div>
            <button onClick={() => deleteNote(curpost.id)}> Delete</button>
            <h1 onDoubleClick={() => setIsEditing(true)}>{curpost.title}</h1>
          </div>
      }
    </li>
  );
  return (
    <ul>{noteItems}</ul>

  );
}
export default App;
